import RequestBase from '../../../common/request_base';
class CaptureRequest extends RequestBase {
    constructor(token, buyOrder, authorizationCode, amount) {
        super(`/rswebpaytransaction/api/webpay/v1.0/transactions/${token}/capture`, 'PUT');
        this.buyOrder = buyOrder;
        this.authorizationCode = authorizationCode;
        this.amount = amount;
    }
    toJson() {
        return JSON.stringify({
            buy_order: this.buyOrder,
            authorization_code: this.authorizationCode,
            capture_amount: this.amount,
        });
    }
}
export { CaptureRequest };
